changeSet.sql:
CREATE TABLE survey_metadata ( id bigserial PRIMARY KEY, file_id bigint UNIQUE NOT NULL, location text NOT NULL, survey_date date NOT NULL, source text, name text, coll_Name text, authority text, habitat text, comments text, coll_date text, status text, xyz_file text, vessel text, z_datum text, z_app text, z_tech text, geom geometry(Geometry,4326) CHECK (st_isvalid(geom)),
CONSTRAINT survey_metadata_file_fk FOREIGN KEY (file_id) REFERENCES indexed_file (id) MATCH SIMPLE ON UPDATE CASCADE ON DELETE CASCADE );
CREATE INDEX survey_metadata_gist_idx ON survey_metadata USING GIST (geom);

DROP VIEW IF EXISTS aodn_nsw_oeh_bathy_map ;
ALTER TABLE survey_metadata DROP COLUMN name, DROP COLUMN coll_Name, DROP COLUMN habitat, ADD COLUMN area text, ADD COLUMN areainfo text, ADD COLUMN area_ha double precision, ADD COLUMN xya_file text, ADD COLUMN survey text, ADD COLUMN max_res text, ADD COLUMN process text;
ALTER TABLE survey_metadata RENAME COLUMN comments TO comment;
ALTER TABLE survey_metadata RENAME COLUMN z_app TO z_acc;

ALTER TABLE survey_metadata
  ADD CONSTRAINT survey_metadata_location_date_key UNIQUE (location, survey_date),
  ALTER COLUMN geom SET NOT NULL;
